// Get data from the API response
const responseData = $input.item.json.Data.Data;

// Transform data for Snowflake
const snowflakeData = responseData.map(record => {
  // Convert Unix timestamp to Date object
  const timestamp = record.time * 1000; // Convert to milliseconds
  const dateObj = new Date(timestamp);

  // Format date as YYYY-MM-DD for Snowflake
  const date = dateObj.toISOString().split('T')[0];

  // Format hour as HH
  const hour = dateObj.getUTCHours().toString().padStart(2, '0');

  // Return object with proper naming for Snowflake columns
  return {
    TIME_UNIX: record.time,
    DATE_STR: date,
    HOUR_STR: hour,
    OPEN_PRICE: record.open,
    HIGH_PRICE: record.high,
    CLOSE_PRICE: record.close,
    LOW_PRICE: record.low,
    VOLUME_FROM: record.volumefrom,
    VOLUME_TO: record.volumeto
  };
});

// Generate SQL MERGE statement for Snowflake
let sqlQueries = [];

// Generate a unique batch ID for logging
const batchId = Date.now().toString();

// Create individual merge statements for each record
snowflakeData.forEach((record, index) => {
  // Format values properly for SQL
  const timeUnix = record.TIME_UNIX;
  const dateStr = `'${record.DATE_STR}'`;
  const hourStr = `'${record.HOUR_STR}'`;
  const openPrice = record.OPEN_PRICE;
  const highPrice = record.HIGH_PRICE;
  const closePrice = record.CLOSE_PRICE;
  const lowPrice = record.LOW_PRICE;
  const volumeFrom = record.VOLUME_FROM;
  const volumeTo = record.VOLUME_TO;

  // Create MERGE statement for this record
  const mergeQuery = `
-- MERGE statement for timestamp ${timeUnix} (${record.DATE_STR} ${record.HOUR_STR}:00)
MERGE INTO BITCOIN_DATA.PRICES.HOURLY_PRICES AS target
USING (SELECT 
  ${timeUnix} AS time_unix, 
  ${dateStr}::DATE AS date_str, 
  ${hourStr} AS hour_str, 
  ${openPrice} AS open_price, 
  ${highPrice} AS high_price, 
  ${closePrice} AS close_price, 
  ${lowPrice} AS low_price, 
  ${volumeFrom} AS volume_from,
  ${volumeTo} AS volume_to
) AS source
ON target.time_unix = source.time_unix
WHEN MATCHED THEN UPDATE SET
  target.open_price = source.open_price,
  target.high_price = source.high_price,
  target.close_price = source.close_price,
  target.low_price = source.low_price,
  target.volume_from = source.volume_from
WHEN NOT MATCHED THEN INSERT
  (time_unix, date_str, hour_str, open_price, high_price, close_price, low_price, volume_from, volume_to)
VALUES
  (source.time_unix, source.date_str, source.hour_str, source.open_price, source.high_price, source.close_price, source.low_price, source.volume_from, source.volume_to);`;

  sqlQueries.push(mergeQuery);
});

// Create a single bulk insert query as an alternative approach
const bulkValues = snowflakeData.map(record => {
  return `(${record.TIME_UNIX}, '${record.DATE_STR}', '${record.HOUR_STR}', ${record.OPEN_PRICE}, ${record.HIGH_PRICE}, ${record.CLOSE_PRICE}, ${record.LOW_PRICE}, ${record.VOLUME_FROM}, ${record.VOLUME_TO}, CURRENT_TIMESTAMP())`;
}).join(',\n  ');

const bulkMergeQuery = `
-- Bulk MERGE for all records in batch ${batchId}
MERGE INTO BITCOIN_DATA.PRICES.HOURLY_PRICES AS target
USING (
  SELECT column1 AS time_unix, 
         column2 AS date_str, 
         column3 AS hour_str, 
         column4 AS open_price, 
         column5 AS high_price, 
         column6 AS close_price, 
         column7 AS low_price, 
         column8 AS volume_from, 
         column9 AS volume_to
  FROM VALUES
  ${bulkValues}
) AS source
ON target.time_unix = source.time_unix
WHEN MATCHED THEN UPDATE SET
  target.open_price = source.open_price,
  target.high_price = source.high_price,
  target.close_price = source.close_price,
  target.low_price = source.low_price,
  target.volume_from = source.volume_from,
  target.volume_to = source.volume_to
WHEN NOT MATCHED THEN INSERT
  (time_unix, date_str, hour_str, open_price, high_price, close_price, low_price, volume_from, volume_to)
VALUES
  (source.time_unix, source.date_str, source.hour_str, source.open_price, source.high_price, source.close_price, source.low_price, source.volume_from, source.volume_to);
`;

// Return both the transformed data and the SQL queries
return { 
  json: { 
    records: snowflakeData,
    individualMergeQueries: sqlQueries,
    bulkMergeQuery: bulkMergeQuery,
    recordCount: snowflakeData.length,
    batchId: batchId,
    timestamp: new Date().toISOString()
  } 
};

// Get the results from whichever method was used
const input = $input.all();
let snowflakeResults;
let methodUsed;

// Determine which method was used
if (input.length > 0 && input[0].json) {
  if (input[0].json.hasOwnProperty('ROWS_AFFECTED')) {
    // This is from the SQL execution method
    snowflakeResults = input[0].json;
    methodUsed = 'SQL Execution';
  } else {
    // This is from the Insert method
    snowflakeResults = input[0].json;
    methodUsed = 'Insert Operation';
  }
}

// Get original data info
const recordCount = $('Transform Data with SQL').item.json.recordCount;
const batchId = $('Transform Data with SQL').item.json.batchId;

// Process results
let result;

if (snowflakeResults) {
  // Handle SQL execution results
  if (methodUsed === 'SQL Execution') {
    result = {
      success: true,
      method: methodUsed,
      message: `Successfully executed SQL for ${recordCount} Bitcoin hourly records`,
      details: snowflakeResults,
      rowsAffected: snowflakeResults.ROWS_AFFECTED || 0,
      timestamp: new Date().toISOString(),
      batchId: batchId
    };
  } 
  // Handle Insert operation results
  else {
    const insertedRecords = Array.isArray(snowflakeResults) ? snowflakeResults.length : 0;

    result = {
      success: true,
      method: methodUsed,
      message: `Successfully processed ${recordCount} Bitcoin hourly records`,
      details: `${insertedRecords} records inserted/updated in Snowflake`,
      timestamp: new Date().toISOString(),
      batchId: batchId
    };
  }
} else {
  // Handle no results (error case)
  result = {
    success: false,
    message: "Error processing data in Snowflake",
    timestamp: new Date().toISOString(),
    batchId: batchId
  };
}

return { json: result };

