return {
  GITHUB_USERNAME: "YOUR_GITHUB_USERNAME",
  GITHUB_REPO: "YOUR_REPOSITORY_NAME",
  SNOWFLAKE_DATABASE: "BITCOIN_DATA",
  SNOWFLAKE_SCHEMA: "PRICES",
  SNOWFLAKE_TABLE: "HOURLY_PRICES",
  BACKUP_FOLDER: "daily_backups"
};

const allInputs = $input.all();

if (!Array.isArray(allInputs) || allInputs.length === 0) {
  return [{ json: { success: false, message: 'No data received in inputs' } }];
}

// CSV header
let csvContent = "TIME_UNIX,DATE_STR,HOUR_STR,OPEN_PRICE,HIGH_PRICE,CLOSE_PRICE,LOW_PRICE,VOLUME_FROM,VOLUME_TO\n";

// Add each row
allInputs.forEach(item => {
  const row = item.json;
  const line = [
    row.TIME_UNIX ?? '',
    row.DATE_STR ?? '',
    row.HOUR_STR ?? '',
    row.OPEN_PRICE ?? '',
    row.HIGH_PRICE ?? '',
    row.CLOSE_PRICE ?? '',
    row.LOW_PRICE ?? '',
    row.VOLUME_FROM ?? '',
    row.VOLUME_TO ?? ''
  ].join(',');

  csvContent += line + '\n';
});

// Date for filename
const now = new Date();
const dateStr = now.toISOString().split('T')[0];

return [{
  json: {
    csvContent,
    dateStr,
    recordCount: allInputs.length,
    filename: `btc_last24h_${dateStr}.csv`,
    backupTimestamp: now.toISOString(),
    success: true
  }
}];
